// Generated by re2zig
// re2zig $INPUT -o $OUTPUT

const std = @import("std");

const none = std.math.maxInt(usize);

const SemVer = struct {
    major: u32,
    minor: u32,
    patch: u32,
};

fn s2n(str: []const u8) u32 { // convert pre-parsed string to a number
    var n: u32 = 0;
    for (str) |c| { n = n * 10 + (c - 48); }
    return n;
}

